/// <reference path="./src/lib.kso.d.ts" />
/// <reference path="./src/lib.wps.d.ts" />

declare interface ApplicationEx_Wps extends Wps.WpsApplication{
    /**
     * wps表格的Application对象
     */
    WpsApplication(): Wps.WpsApplication;

    /**
     * 用于对ribbon界面进行更新
     * @param id
     */
    UpdateRibbon(id?: number): void;

    /**
     * 弹出对话框
     * @param url html文件的路径
     * @param title 对话框标题
     * @param width 对话框宽度
     * @param height 对话框高度
     * @param isModal 是否模态
     */
    ShowDialog(url: string, title?: string, width?: number, height?: number, isModal?: boolean): void;
}

declare interface Window{
    readonly Application: ApplicationEx_Wps;
}
